BEGIN TRANSACTION;;
CREATE TABLE "accounts_user" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "password" varchar(128) NOT NULL, "last_login" datetime NULL, "is_superuser" bool NOT NULL, "email" varchar(254) NOT NULL UNIQUE, "nom" varchar(100) NOT NULL, "prenom" varchar(100) NOT NULL, "telephone" varchar(20) NULL, "role" varchar(10) NOT NULL, "is_active" bool NOT NULL, "is_staff" bool NOT NULL, "date_joined" datetime NOT NULL, "updated_at" datetime NOT NULL);;
CREATE TABLE "accounts_user_groups" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "user_id" bigint NOT NULL REFERENCES "accounts_user" ("id") DEFERRABLE INITIALLY DEFERRED, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED);;
CREATE TABLE "accounts_user_user_permissions" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "user_id" bigint NOT NULL REFERENCES "accounts_user" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);;
CREATE TABLE "auth_group" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "name" varchar(150) NOT NULL UNIQUE);;
CREATE TABLE "auth_group_permissions" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);;
CREATE TABLE "auth_permission" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "content_type_id" integer NOT NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "codename" varchar(100) NOT NULL, "name" varchar(255) NOT NULL);;
CREATE TABLE "authtoken_token" ("key" varchar(40) NOT NULL PRIMARY KEY, "created" datetime NOT NULL, "user_id" bigint NOT NULL UNIQUE REFERENCES "accounts_user" ("id") DEFERRABLE INITIALLY DEFERRED);;
CREATE TABLE "django_admin_log" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "object_id" text NULL, "object_repr" varchar(200) NOT NULL, "action_flag" smallint unsigned NOT NULL CHECK ("action_flag" >= 0), "change_message" text NOT NULL, "content_type_id" integer NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" bigint NOT NULL REFERENCES "accounts_user" ("id") DEFERRABLE INITIALLY DEFERRED, "action_time" datetime NOT NULL);;
CREATE TABLE "django_content_type" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "app_label" varchar(100) NOT NULL, "model" varchar(100) NOT NULL);;
CREATE TABLE "django_migrations" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "app" varchar(255) NOT NULL, "name" varchar(255) NOT NULL, "applied" datetime NOT NULL);;
CREATE TABLE "django_session" ("session_key" varchar(40) NOT NULL PRIMARY KEY, "session_data" text NOT NULL, "expire_date" datetime NOT NULL);;
CREATE TABLE "logements_avis" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "note" integer NOT NULL, "commentaire" text NOT NULL, "created_at" datetime NOT NULL, "client_id" bigint NOT NULL REFERENCES "accounts_user" ("id") DEFERRABLE INITIALLY DEFERRED, "reservation_id" bigint NOT NULL UNIQUE REFERENCES "reservations_reservation" ("id") DEFERRABLE INITIALLY DEFERRED, "logement_id" bigint NOT NULL REFERENCES "logements_logement" ("id") DEFERRABLE INITIALLY DEFERRED);;
CREATE TABLE "logements_logement" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "titre" varchar(200) NOT NULL, "description" text NOT NULL, "type" varchar(20) NOT NULL, "adresse" varchar(255) NOT NULL, "ville" varchar(100) NOT NULL, "code_postal" varchar(10) NOT NULL, "prix_par_nuit" decimal NOT NULL, "capacite_max" integer NOT NULL, "nombre_chambres" integer NOT NULL, "nombre_salles_bain" integer NOT NULL, "photo_url" varchar(200) NULL, "wifi" bool NOT NULL, "parking" bool NOT NULL, "cuisine" bool NOT NULL, "animaux" bool NOT NULL, "actif" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "hote_id" bigint NOT NULL REFERENCES "accounts_user" ("id") DEFERRABLE INITIALLY DEFERRED);;
CREATE TABLE "reservations_reservation" ("id" integer NOT NULL PRIMARY KEY AUTO_INCREMENT, "date_debut" date NOT NULL, "date_fin" date NOT NULL, "nombre_voyageurs" integer NOT NULL, "prix_total" decimal NOT NULL, "statut" varchar(20) NOT NULL, "message" text NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "client_id" bigint NOT NULL REFERENCES "accounts_user" ("id") DEFERRABLE INITIALLY DEFERRED, "logement_id" bigint NOT NULL REFERENCES "logements_logement" ("id") DEFERRABLE INITIALLY DEFERRED);;
